mapper.select:
select
id, type, title, content, time, author_id, class_ids, ext, create_time, update_time
from ms_zhou.notify


        and id = #{id}


        and type = #{type}


        and title = #{title}


        and content = #{content}


        and time = #{time}


        and author_id = #{authorId}


        and class_ids = #{classIds}


        and ext = #{ext}


        and create_time = #{createTime}


        and update_time = #{updateTime}
